// --------------------------------------------------
const WELCOME_MESSAGES = {
    ko: "í•˜ë‚˜ë‹˜ì˜ ì€í˜œì™€ ì¶•ë³µì´ ë„˜ì¹˜ì‹œê¸¸.",
    en: "May God's grace and blessings overflow.",
    zh: "æ„¿ä¸Šå¸çš„æ©å…¸å’Œç¥ç¦ä¸æ‚¨åŒåœ¨ã€‚",
    ru: "ĞŸÑƒÑÑ‚ÑŒ Ğ±Ğ»Ğ°Ğ³Ğ¾Ğ´Ğ°Ñ‚ÑŒ Ğ¸ Ğ±Ğ»Ğ°Ğ³Ğ¾ÑĞ»Ğ¾Ğ²ĞµĞ½Ğ¸Ğµ Ğ‘Ğ¾Ğ¶ÑŒĞµ Ğ¿Ñ€ĞµĞ±ÑƒĞ´ÑƒÑ‚ Ñ Ğ²Ğ°Ğ¼Ğ¸.",
    vi: "Nguyá»‡n Ã¢n Ä‘iá»ƒn vÃ  phÆ°á»›c lÃ nh cá»§a ChÃºa tuÃ´n trÃ n.",
    default: "May God's grace and blessings overflow."
};

/**
 * RAG ê²€ìƒ‰ ë‹¨ê³„: í‚¤ì›Œë“œ ë§¤ì¹­ì„ í†µí•´ ê´€ë ¨ì„±ì´ ë†’ì€ ì§€ì‹ ì²­í¬ë¥¼ ê²€ìƒ‰í•©ë‹ˆë‹¤.
 * @param {string} query ì‚¬ìš©ì ì§ˆë¬¸
 * @param {string} currentLang í˜„ì¬ ì–¸ì–´ ì½”ë“œ
 * @returns {string} ê²€ìƒ‰ëœ ì»¨í…ìŠ¤íŠ¸ë¥¼ í¬ë§·íŒ…ëœ ë¬¸ìì—´ë¡œ ë°˜í™˜
 */
function retrieveRelevantContext(query, currentLang) {
    const queryLower = query.toLowerCase();
    
    // ì¿¼ë¦¬ë¥¼ ë‹¨ì–´ ë‹¨ìœ„ë¡œ ë¶„ë¦¬í•˜ê³  ë¶ˆìš©ì–´(ì¡°ì‚¬, ì¼ë°˜ ë‹¨ì–´)ë¥¼ ì œê±°í•˜ê³ , í•µì‹¬ í‚¤ì›Œë“œ ë§¤ì¹­ì„ ê°•í™”í•©ë‹ˆë‹¤.
    const queryWords = queryLower.split(/\s+/).filter(word => word.length > 1 && !['ì€', 'ëŠ”', 'ì´', 'ê°€', 'ì˜', 'ì—', 'ìš”', 'ë‹¤', 'ì„', 'ë¥¼', 'ì—ì„œ', 'ê°€ìš”', 'ì¸ê°€ìš”', 'í•˜ê³ ', 'ì‹¶ì€ë°', 'ë°©ë²•ì´', 'ìˆëŠ”ì§€', 'ì£¼ì„¸ìš”', 'ë²•', 'ì•Œë ¤'].includes(word));
    
    let relevantChunks = [];

    for (const item of KNOWLEDGE_BASE) {
        let matchScore = 0;
        const itemKeywordsLower = item.keywords.map(k => k.toLowerCase());

        // ì¿¼ë¦¬ì˜ ê° ë‹¨ì–´ê°€ í‚¤ì›Œë“œì— í¬í•¨ë˜ëŠ”ì§€ í™•ì¸í•˜ì—¬ ì ìˆ˜ ë¶€ì—¬
        queryWords.forEach(queryWord => {
            if (itemKeywordsLower.some(keyword => keyword.includes(queryWord) || queryWord.includes(keyword))) {
                matchScore += 1;
            }
        });
        
        if (matchScore > 0) {
            relevantChunks.push({
                ...item,
                score: matchScore
            });
        }
    }
    
    // ì ìˆ˜ê°€ ë†’ì€ ìˆœìœ¼ë¡œ ì •ë ¬í•©ë‹ˆë‹¤.
    relevantChunks.sort((a, b) => b.score - a.score);
    
    // ğŸš¨ [ìˆ˜ì •]: currentLangì— ë§ëŠ” topicê³¼ contentë¥¼ ê°€ì ¸ì˜¤ê±°ë‚˜, ì—†ìœ¼ë©´ koë¡œ í´ë°±í•©ë‹ˆë‹¤.
    return relevantChunks.slice(0, 3).map(item => {
        const topic = item.topic[currentLang] || item.topic.ko;
        const content = item.content[currentLang] || item.content.ko;
        return `[${topic}]: ${content}`;
    }).join('\n---\n');
}

